	@Override
	public boolean touchUp(int screenX, int screenY, int pointer, int button) {
		// TODO Auto-generated method stub
		Gdx.app.log("TouchUp", " up");
		touchedUp = true;
		
		return false;
	}
	
	public boolean isTouchedUp () {
		if(touchedUp) {
			touchedUp = false;
			return true;
		}
		
		return false;
	}

